 var conn = require('../mysql');

 /* Create database function */
 module.exports.create_db = function(db_host, db_name, db_userid, db_passwd) {
 	var connection = conn.createConnection ({
 		host: db_host,
 		user: db_userid,
 		password: db_passwd,
 	});
 	connection.connect();
 	var query = 'CREATE DATABASE ' + db_name;
 	connection.query(query, function(err) {
 		if(err) {
 			throw err;
 		}
 	})

 	console.log("Success database");
 }
